import com.eviware.soapui.support.GroovyUtils
def gUtil = new GroovyUtils(context)
//import request
def getCustReqXml = context.expand('${GetCustById#Request}')

// create an xml holder to get Getcustomerbyid result value
def Xmlhol1 = gUtil.getXmlHolder(getCustReqXml)
//Xmlhol1.namespaces['ns1'] = "http://tempuri.org/"
def cust_Id = Xmlhol1.getNodeValue("//*:customerId")
log.info(cust_Id)
//Resonse
def getCustResXml = context.expand('${GetCustById#Response}')

def holder = gUtil.getXmlHolder(getCustResXml)

assert holder.getNodeValue("//*:id")== cust_Id

